$tw.hooks.addHook("th-editing-tiddler", function(event) {
	var targetTitle = event.tiddlerTitle;
	var stories = $tw.wiki.getTiddlerList('$:/_sq/Stories/StoriesList');
	var draftTitle = $tw.wiki.findDraft(targetTitle);
	var shiftKey = (event && event.event) ? event.event.shiftKey : null;

	//if !draftTitle, its not open anywhere
	//cant be open without existing, but can exist without being opened

	if(!draftTitle && !shiftKey) {
		return true;
	}

	var node = event.navigateFromNode;
	var thisStory = node.getVariable("tv-story-title");
	var otherStory = (stories[0] == thisStory)? stories[1] : stories[0];
	var otherStoryList = $tw.wiki.getTiddlerList(otherStory);

	if(otherStoryList.indexOf(draftTitle) > -1) {
		alert("This tiddler is already open for editing in the other story");
		return false;
	}

	var generateDraftTitle = function(title) {
		var c = 0,
		draftTitle;
		do {
			draftTitle = "Draft " + (c ? (c + 1) + " " : "") + "of '" + title + "'";
			c++;
		} while($tw.wiki.tiddlerExists(draftTitle));
		return draftTitle;
	};

	if(shiftKey) {
    //open in other story
		if(!draftTitle) {
			var tiddler = $tw.wiki.getTiddler(targetTitle);
			draftTitle = generateDraftTitle(targetTitle);
			var draftTiddler = new $tw.Tiddler(
				tiddler,
				{
					title: draftTitle,
					"draft.title": targetTitle,
					"draft.of": targetTitle
				},
				$tw.wiki.getModificationFields()
			);
			$tw.wiki.addTiddler(draftTiddler);
		}
		var otherStoryTiddler = $tw.wiki.getTiddler(otherStory);
		otherStoryList.splice(0 ,0, draftTitle);
		$tw.wiki.addTiddler(new $tw.Tiddler(
			{title: otherStory},
			otherStoryTiddler,
			{list:otherStoryList}
		));
		return false;
	}

	return true;
});

$:/_sq/Stories/LinkKeybindings.text:
$tw.hooks.addHook('th-navigating', function(event) {
	//override core behaviour when shift key was used
	if( ((event.event && event.event.shiftKey) || (event.shiftKey && event)) && event.navigateTo) {
		var stories = $tw.wiki.getTiddlerList('$:/_sq/Stories/StoriesList');
		var node = event.navigateFromNode;
		var thisStory = node.getVariable("tv-story-list");
		var otherStory = (stories[0] == thisStory)? stories[1] : stories[0];	
		var storyList = $tw.wiki.getTiddlerList(otherStory);
		var slot = storyList.indexOf(event.navigateTo);

		if(slot < 0){
			if($tw.wiki.getTiddlerText("$:/config/Navigation/openLinkFromOutsideRiver") === "bottom") {

				storyList.splice(storyList.length, 0, event.navigateTo);
			} else {
				storyList.splice(0, 0, event.navigateTo);
			}
			var storyTiddler =	$tw.wiki.getTiddler(otherStory);
			$tw.wiki.addTiddler(new $tw.Tiddler(
				{title: otherStory},
				storyTiddler,
				{list:storyList}
			));
		}
		var histories = $tw.wiki.getTiddlerList('$:/_sq/Stories/HistoriesList');
		var thisHistory = node.getVariable("tv-history-list");
		var otherHistory = (histories[0] == thisHistory) ? histories[1] : histories[0];		
		$tw.wiki.addToHistory(event.navigateTo,event.navigateFromClientRect, otherHistory); 
		if($tw.wiki.getTiddlerText("$:/config/_sq/Stories/story2") === "no") {
			$tw.wiki.setText("$:/config/_sq/Stories/story2",undefined,undefined,"yes",undefined);
		}
		event.navigateTo = false;
	}
	return event;
});

