\define sidebarbreakpoint()
<$text text={{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}/>
\end

\define sidebarwidth()
<$text text={{$:/themes/tiddlywiki/vanilla/metrics/sidebarwidth}}/>
\end

\define leftsidebarwidth()
<$text text={{$:/xp/LeftOpenTab/metrics/width}}/>
\end

\define storyriverpaddingleft()
<$text text={{$:/xp/LeftOpenTab/metrics/storyriverpaddingleft}}/>
\end

<pre>/** FOR STORIES PLUGIN
 *  https://giffmex.org/stroll/empty.html#%24%3A%2Fplugins%2Fsq%2FStories
 *  this stylesheet will apply with the existence of the plugin
 */

<$list filter="[[$:/plugins/sq/Stories]is[tiddler]]" variable="chk">
@media (min-width: <<sidebarbreakpoint>>) {
    .tc-story-river {
        transition: width 250ms, padding 250ms;
    <$reveal state="$:/config/_sq/Stories/story2" type="nomatch" text="no" default="no">
        <$list filter="[list[$:/_sq/Stories/Story2StoryList]limit[1]]">
            <$reveal state="$:/state/sidebar" type="match" text="yes" default="yes">
        width: calc((100% - <<sidebarwidth>> - <<leftsidebarwidth>> + <<storyriverpaddingleft>> + 2px)*0.5) !important;
        left: <<storyriverpaddingleft>>;

            /* Compatible with $:/xp/switchlayout */
            <$list filter="[[$:/themes/tiddlywiki/vanilla/options/sidebarlayout]get[text]] +[else[fluid-fixed]match[fixed-fluid]]" variable="ignore">
        width: calc(({{$:/themes/tiddlywiki/vanilla/metrics/storyright}} - <<leftsidebarwidth>> + 22px)*0.5) !important;
        margin-left: calc(<<leftsidebarwidth>> - <<storyriverpaddingleft>>) !important;
            </$list>
            </$reveal>
            <$reveal state="$:/state/sidebar" type="match" text="no" default="yes">
        width: calc((100% - <<leftsidebarwidth>> + 20px)*0.5) !important;
        left: <<storyriverpaddingleft>>;

            /* Compatible with $:/xp/switchlayout */
            <$list filter="[[$:/themes/tiddlywiki/vanilla/options/sidebarlayout]get[text]] +[else[fluid-fixed]match[fixed-fluid]]" variable="ignore">
        margin-left: calc(<<storyriverpaddingleft>> + 172px) !important;
            </$list>
            </$reveal>
        </$list>
    </$reveal>
    }

    <$reveal state="$:/config/_sq/Stories/story2" type="nomatch" text="no" default="no">
    .tc-story-river.tc-storytwo-river {
        height: 0 !important;
        width: 0 !important;
        padding: 0 !important;
        margin: 0 !important;
    }

    .sq-story-rivertwo-scrollable {
        top: 0;
        width: calc(100% - <<sidebarwidth>> - <<leftsidebarwidth>> - <<storyriverpaddingleft>>);
        transition: width 250ms, padding 250ms, right 250ms;
    <$list filter="[[$:/StoryList]field:list[]]">
        left: calc(<<leftsidebarwidth>> + <<storyriverpaddingleft>>) !important;
    </$list>
    <$list filter="[list[$:/StoryList]limit[1]]">
        left: unset !important;
            <$reveal state="$:/state/sidebar" type="match" text="yes" default="yes">
        width: calc((100% - <<sidebarwidth>> - <<leftsidebarwidth>> + 2px)*0.5) !important;
        padding: 1em 0 42px 0;
        margin-left: -16px;

            /* Compatible with $:/xp/switchlayout */
            <$list filter="[[$:/themes/tiddlywiki/vanilla/options/sidebarlayout]get[text]] +[else[fluid-fixed]match[fixed-fluid]]" variable="ignore">
        width: calc(({{$:/themes/tiddlywiki/vanilla/metrics/storyright}} - <<leftsidebarwidth>> - 20px)*0.5) !important;
            </$list>
            </$reveal>
            <$reveal state="$:/state/sidebar" type="match" text="no" default="yes">
        width: calc((100% - <<leftsidebarwidth>> - <<storyriverpaddingleft>> + 20px)*0.5) !important;
        padding: 1em 0 42px 0;
        right: 12px !important;
            </$reveal>
    </$list>
    }
    </$reveal>

    .tc-tiddler-frame {
        width: 100% !important;
    }
}
</$list>

</pre>

$:/xp/LeftOpenTab/styles.text:
\define sidebarbreakpoint()
<$text text={{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}/>
\end

<pre>.leftopentab-content {
    position: fixed;
    max-width: {{$:/xp/LeftOpenTab/metrics/width}};
    left: 0px;
    top: 0px;
    bottom: 0;
    padding: {{$:/xp/LeftOpenTab/metrics/paddingtop}} {{$:/xp/LeftOpenTab/metrics/paddingright}} 28px 10px;
    background: none;
    height: 100vh;
    overflow: auto;
    -webkit-overflow-scrolling: touch;
}

@media (max-width: <<sidebarbreakpoint>>) {
    .leftopentab-content {
        display: block;
        background: <<colour page-background>>;
        max-width: 80vw;
        max-height: 60vh;
        <<box-shadow "1px 1px 5px rgba(0, 0, 0, 0.3)">>
    }
}

/**  Hide scrollbar  **/

/* Firefox */
.leftopentab-content {
    scrollbar-width: {{$:/config/LeftOpenTab/Scrollbar}};
    scrollbar-color: {{$:/config/LeftOpenTab/ScrollbarColor}};
}

/* Chrome */
.leftopentab-content::-webkit-scrollbar {
    background: none;
    width: 8px;
    display: {{$:/config/LeftOpenTab/Scrollbar}};
}

.leftopentab-content::-webkit-scrollbar-thumb {
    -webkit-border-radius: 9px;
    border-radius: 9px;
    background: <<colour sidebar-muted-foreground>>;
}

/**  Title style  **/

.leftopentab-title-line,
.leftopentab-title-semicircle,
.leftopentab-title-square {
    padding: 3px 0 0 0;
    text-align: center;
    width: 230px;
    color: <<colour sidebar-tab-foreground>>;
}

.leftopentab-title-line {
    padding: 3px 0 1px 0;
    border-bottom: 1px solid <<colour sidebar-muted-foreground>>;
}

.leftopentab-title-semicircle {
    border-radius: 15px 15px 100px 100px;
    background-color: #eee;
    backdrop-filter: blur(10px);
}

.leftopentab-title-square {
    background-color: rgba(255, 255, 255, 0.6);
    backdrop-filter: blur(10px);
}

.leftopentab-title-word {
    padding: 6px 0 0 0;
    text-align: left;
    margin-left: 0.3em;
    color: <<colour sidebar-tab-foreground>>;
    font-size: 1em;
}

/**  Tiddler title style  **/

.xp-highlight-title {
    color: {{$:/config/LeftOpenTab/Color}} !important;
}

/**  Horizontal-rule style  **/

.leftopentab-hr-style {
    max-width: calc({{$:/xp/LeftOpenTab/metrics/width}} - {{$:/xp/LeftOpenTab/metrics/storyriverpaddingleft}} / 2);
    margin: 1px 0 4px;
    border-top: 1px solid <<colour sidebar-muted-foreground>>;
}

/**  compatible-with-Stories-plugin  **/

.tc-image-bars {
    padding: 0px;
    color: <<colour muted-foreground>>;
    fill: <<colour muted-foreground>>;
    font-size: 0.9em;
}

.tc-image-bars img {
    width: 13.5pt;
    height: 13.5pt;
}
</pre>
