$:/xp/quickbar/styles/dropup.text:
.xp-drop-up-wrapper {
	position: relative;
}

.xp-drop-up {
	min-width: 380px;
	border: 1px solid <<colour dropdown-border>>;
	background-color: <<colour dropdown-background>>;
	padding: 7px 0 7px 0;
	margin: 4px 0 0 0;
	white-space: nowrap;
	text-shadow: none;
	line-height: 1.4;

	position: absolute;
    z-index: 600;
    bottom: 100%;
    margin-bottom: 30px;
}

.xp-drop-up .xp-drop-up {
	margin-left: 14px;
}

.xp-drop-up button svg, .xp-drop-up a svg  {
	fill: <<colour foreground>>;
}

.xp-drop-up button:disabled svg {
	fill: <<colour muted-foreground>>;
}

.xp-drop-up button.tc-btn-invisible:hover svg {
	fill: <<colour background>>;
}

.xp-drop-up .xp-drop-up-info {
	padding-left: 14px;
}

.xp-drop-up p {
	padding: 0 14px 0 14px;
}

.xp-drop-up svg {
	width: 1em;
	height: 1em;
}

.xp-drop-up img {
	width: 1em;
}

.xp-drop-up a, .xp-drop-up button {
	display: block;
	padding: 0 14px 0 14px;
	width: 100%;
	text-align: left;
	color: <<colour foreground>>;
	line-height: 1.4;
}

.xp-drop-up .tc-tab-set .tc-tab-buttons button {
	display: inline-block;
	width: auto;
	margin-bottom: 0px;
	border-bottom-left-radius: 0;
	border-bottom-right-radius: 0;
}

.xp-drop-up .tc-prompt {
	padding: 0 14px;
}

.xp-drop-up .tc-chooser {
	border: none;
}

.xp-drop-up .tc-chooser .tc-swatches-horiz {
	font-size: 0.4em;
	padding-left: 1.2em;
}

.xp-drop-up .tc-file-input-wrapper {
	width: 100%;
}

.xp-drop-up .tc-file-input-wrapper button {
	color: <<colour foreground>>;
}

.xp-drop-up a:hover, .xp-drop-up button:hover, .xp-drop-up .tc-file-input-wrapper:hover button {
	color: <<colour tiddler-link-background>>;
	background-color: <<colour tiddler-link-foreground>>;
	text-decoration: none;
}

.xp-drop-up .tc-tab-buttons button {
	background-color: <<colour dropdown-tab-background>>;
}

.xp-drop-up .tc-tab-buttons button.tc-tab-selected {
	background-color: <<colour dropdown-tab-background-selected>>;
	border-bottom: 1px solid <<colour dropdown-tab-background-selected>>;
}

.xp-drop-up-bullet {
	display: inline-block;
	width: 0.5em;
}

.xp-drop-up .tc-tab-contents a {
	padding: 0 0.5em 0 0.5em;
}

$:/xp/quickbar/styles.text:
\define sidebarbreakpoint()
<$text text={{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}/>
\end

nav.tc-quickbar {
	position: fixed;
	z-index: 850;
	display: inline-block;
	bottom: 0;
	right: 0;
	left: 0;
	width: calc({{$:/xp/quickbar/width}});
	margin: auto;
	opacity: 0.75;
	background: rgba(255, 255, 255, 0.5);
	backdrop-filter: blur(10px);
	-webkit-backdrop-filter: blur(10px);
	border-radius: 40px / 50px;
	text-align: center;
}

nav.tc-quickbar:hover {
	opacity: 1;
}

nav.tc-quickbar ul.tc-quickbar-list {
	position: relative;
	list-style-type: none;
	margin: 0;
	padding: 0 12px 0 12px;
	background: <<colour background>>;
	background: <<colour quickbar-background>>;
	<<box-shadow "1px 1px 5px rgba(0, 0, 0, 0.3)">>
	border-radius: 40px / 50px;
}

nav.tc-quickbar li.tc-quickbar-item {
	display: inline-block;
	margin: 0;
	padding: 0;
}

nav.tc-quickbar li.tc-quickbar-item > a,
nav.tc-quickbar li.tc-quickbar-item > button {
	display: inline-block;
/*	text-transform: uppercase; */
	line-height: 1;
	font-weight: 700;
	color: <<colour foreground>>;
	color: <<colour quickbar-foreground>>;
	fill: <<colour foreground>>;
	fill: <<colour quickbar-foreground>>;
	text-decoration: none;
	padding: 0.5em;
	margin: 0;
	background: none;
	border: none;
	cursor: pointer;
	border-radius: 0;
	text-decoration: none;
}

nav.tc-quickbar li.tc-quickbar-item > a.tc-selected,
nav.tc-quickbar li.tc-quickbar-item > button.tc-selected {
	background: <<colour foreground>>;
	background: <<colour quickbar-foreground>>;
	color: <<colour background>>;
	color: <<colour quickbar-background>>;
	fill: <<colour background>>;
	fill: <<colour quickbar-background>>;
}

nav.tc-quickbar li.tc-quickbar-item svg {
	transition: none;
	width: 1em;
	height: 1em;
	fill: <<colour foreground>>;
	fill: <<colour quickbar-foreground>>;
}

nav.tc-quickbar li.tc-quickbar-item .tc-quickbar-dropup-arrow svg {
	width: 0.5em;
	height: 0.5em;
}

nav.tc-quickbar li.tc-quickbar-item > a.tc-selected svg,
nav.tc-quickbar li.tc-quickbar-item > button.tc-selected svg {
	fill: <<colour background>>;
	fill: <<colour quickbar-background>>;
}

nav.tc-quickbar li.tc-quickbar-item > a:hover,
nav.tc-quickbar li.tc-quickbar-item > button:hover svg,
nav.tc-quickbar li.tc-quickbar-item > button:hover {
	background: <<colour foreground>>;
	background: <<colour quickbar-foreground>>;
	color: <<colour background>>;
	color: <<colour quickbar-background>>;
	fill: <<colour background>>;
	fill: <<colour quickbar-background>>;
	border-radius: 0;
	text-decoration: none;
}

nav.tc-quickbar li.tc-quickbar-item > a:active,
nav.tc-quickbar li.tc-quickbar-item > button:active svg,
nav.tc-quickbar li.tc-quickbar-item > button:active {
	background: <<colour foreground>>;
	background: <<colour quickbar-foreground>>;
	color: <<colour background>>;
	color: <<colour quickbar-background>>;
	fill: <<colour background>>;
	fill: <<colour quickbar-background>>;
	border-radius: 0;
	text-decoration: none;
}

nav.tc-quickbar .xp-drop-up,
nav.tc-quickbar .tc-block-dropdown {
	max-width: 70vw;
	max-height: 70vh;
	overflow: auto;
}

nav.tc-quickbar .xp-drop-up a {
	text-decoration: none;
}

nav.tc-quickbar .xp-drop-up .tc-table-of-contents button {
	display: inline-block;
	width: auto;
}

nav.tc-quickbar .xp-drop-up ol {
	margin: 0;
}

.tc-quickbar-tooltip {
	position: relative;
	display: inline-block;
}

.tc-quickbar-tooltip .tc-quickbar-tooltiptext {
	visibility: hidden;
	background: <<colour background>>;
	color: <<colour sidebar-foreground>>;
	fill: <<colour background>>;
	text-align: left;
	border-radius: 6px;
	padding: 8px 24px;
	min-width: 360px;
	max-height: 60vh;
	overflow-y: auto;

	/* Position the tooltip */
	position: absolute;
	z-index: 600;
	bottom: 100%;
	left: 50%;
	margin-left: -40px;
	box-shadow: 0 4px 10px 0 rgba(0, 0, 0, 0.2), 0 4px 20px 0 rgba(0, 0, 0, 0.19);
}

.tc-quickbar-tooltip .tc-quickbar-tooltiptext img {
	width: 1em;
}

.tc-quickbar-tooltip .tc-quickbar-tooltiptext svg {
	fill: <<colour sidebar-button-foreground>> !important;
	transition: none;
	width: 1em;
	height: 1em;
}

.tc-quickbar-tooltip > a,
.tc-quickbar-tooltip > button {
	display: inline-block;
	padding: 0.5em;
	margin: 0;
}

.tc-quickbar-tooltip:hover .tc-quickbar-tooltiptext {
	visibility: visible;
}

.xp-quickbar-divider {
	border-right: 1px solid <<colour sidebar-tab-divider>>;
	margin-left: 4px;
	margin-right: 8px;
}

.quickbar-active-indicator {
	box-shadow: inset 0 -2px <<colour quickbar-foreground>>;
}

.xp-quickbar-theme-changer {
	width: 100%;
	text-align: left;
	border-bottom: 1px solid <<colour sidebar-tab-divider>>;
	padding: 3px 0;
}


/* ON PC */
@media (min-width: <<sidebarbreakpoint>>) {

	.xp-quickbar-on-phone {
		display: none;
	}

}

/* ON PHONE */
@media (max-width: <<sidebarbreakpoint>>) {

	.xp-quickbar-on-pc {
		display: none;
	}

}

@media print {

	nav.tc-quickbar {
		display: none;
	}

}
